mapper.insert:
INSERT INTO MP_BOARD(    BNO 
					      , TITLE 
					      , CONTENT 
					      , WRITER
					      , REGDATE  )
              VALUES(    #{bno}
              		  , #{title}
              		  , #{content}
              		  , #{writer}
              		  , now()  )

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}
